package co.in.capgemini.calculator;

public class Maths {

	public int add(int i, int j) {
		// TODO Auto-generated method stub
		return i+j;
	}

	public int subtract(int i, int j) {
		// TODO Auto-generated method stub
		return i-j;
	}

	public int multiplication(int i, int j) {
		// TODO Auto-generated method stub
		return i*j;
	}

	public double divide(double d, double e) {
		return d/e;
	}

}
